INSERT INTO song VALUES (1,1,'Hallelujah','Your love is amazing, steady and unchanging','<song>&#10;        <head>&#10;            <title>Hallelujah</title>&#10;            <copyright>&#10;                <author>Brenton Brown</author>&#10;            <author>Brian Doerkson</author>&#10;            <year>1997</year>&#10;            <owner>WorshipBucks Records</owner>&#10;            </copyright>&#10;            <key>G</key>&#10;        </head>&#10;        <lyrics>&#10;        <verse>&#10;            <line>Your love is a<sup text=\"G/B\">mazing,</sup> steady and&#10;            un<sup text=\"Cadd2\">changing,</sup></line>&#10;            <line><sup></sup>Your love is a <sup text=\"D\">mountain,</sup>&#10;            firm beneath my <sup text=\"Cadd2\">feet,</sup> </line>&#10;            <line>Blah</line>&#10;        </verse>&#10;        <chorus>&#10;            <line>Hallelujah,</line>&#10;            <line>Hallelujah,</line>&#10;            <line>Your love makes me sing</line>&#10;        </chorus>&#10;        </lyrics>&#10;    </song>\n');
INSERT INTO song VALUES (2,2,'The Solid Rock','My hope is built on nothing less than Jesus\' blood and righteousness','<song>&#10;        <head>&#10;            <title>The Solid Rock</title>&#10;            <copyright>&#10;                <author>William Mote</author>&#10;            </copyright>&#10;            <key>D</key>&#10;        </head>&#10;        <lyrics>&#10;            <verse>&#10;            <line>My hope is built on nothing less than Jesus\' blood and righteousness,</line>&#10;            <line>No merit of my own I claim, but wholly trust in Jesus\' name,</line>&#10;            </verse>&#10;            <chorus>&#10;                <line>On Christ the solid rock I stand,</line>&#10;                <line>All other ground is sinking sand.</line>&#10;            </chorus>&#10;            <verse>&#10;            <line>When weary in this earthly race, I rest on his unchanging grace,</line>&#10;            <line>Through every wild and stormy gale, my anchor holds through the veil,</line>&#10;            </verse>&#10;            <verse>&#10;            <line>His vow, his covenant and blood are my defence against&#10;            the flood,</line>&#10;            <line>When earthly hopes are swept away, He will uphold me on that day</line>&#10;            </verse>&#10;        </lyrics>&#10;    </song>\n');
INSERT INTO song VALUES (3,3,'Jesus, All For Jesus','Jesus, all for Jesus','<song>&#10;        <head>&#10;            <title>Jesus, All For Jesus</title>&#10;            <copyright>&#10;                <author>Robin Mark</author>&#10;            </copyright>&#10;        </head>&#10;        <lyrics>&#10;            <verse>&#10;                <line>Jesus, all for Jesus,</line>&#10;                <line>All I am and have,</line>&#10;                <line>And ever hope to be.</line>&#10;            </verse>&#10;            <verse>&#10;                <line>All of my ambitions hopes and plans,</line>&#10;                <line>I surrender these,</line>&#10;                <line>Into your hands.</line>&#10;            </verse>&#10;            <bridge>&#10;                <line>For it\'s only in your will that I am free,</line>&#10;                <line>For it\'s only in your will that I am free.</line>&#10;            </bridge>&#10;        </lyrics>&#10;     </song>\n');
